import React, { useState } from "react";
import axios from "axios";
import "./styles.css";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Home from "./Home";
import Auth from "./Auth"; // Your existing login/signup page
import Prediction from "./Prediction"; // Your prediction form

const App = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loggedIn, setLoggedIn] = useState(false);
  const [prediction, setPrediction] = useState(null);

  // ✅ Ensure "Age" is the first feature and included properly
  const [features, setFeatures] = useState({
    Age: "", 
    BMI: "",
    DietQuality: "",
    SleepQuality: "",
    CholesterolTotal: "",
    CholesterolHDL: "",
    CholesterolTriglycerides: "",
    MMSE: "",
    FunctionalAssessment: "",
    MemoryComplaints: "",
    ADL: "",
    BehavioralProblems: "",
  });

  const toggleAuth = () => setIsLogin(!isLogin);

  const handleAuth = async () => {
    const endpoint = isLogin ? "login" : "signup";
    try {
      const response = await axios.post(`http://127.0.0.1:5000/${endpoint}`, {
        email,
        password,
      });
      alert(response.data.message);
      if (isLogin) setLoggedIn(true);
    } catch (error) {
      alert(error.response?.data?.message || "Authentication failed");
    }
  };

  const handlePrediction = async () => {
    try {
      const featureValues = Object.values(features).map(value => (value ? Number(value) : 0)); // Convert all inputs to numbers
      console.log("Sending features:", features); // Debugging log
      const response = await axios.post("http://127.0.0.1:5000/predict", {
        features: featureValues,
      });
      setPrediction(response.data.prediction);
    } catch (error) {
      alert("Prediction failed. Check the inputs and try again.");
    }
  };

  return (
    <div className="container">
    <h1 className="main-heading">Alzheimer's Disease Detection</h1>
      {!loggedIn ? (
        <div className="auth-box">
          <h2>{isLogin ? "Login" : "Sign Up"}</h2>
          <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <button onClick={handleAuth}>{isLogin ? "Login" : "Sign Up"}</button>
          <p onClick={toggleAuth} className="toggle-text">
            {isLogin ? "Don't have an account? Sign Up" : "Already have an account? Login"}
          </p>
        </div>
      ) : (
        <div className="prediction-box">
          
          {Object.entries(features).map(([key, value], index) => (
            <div key={index} className="input-group">
              <label htmlFor={key}>{key}:</label>
              <input
                id={key}
                type="number"
                placeholder={`Enter ${key}`}
                value={value}
                onChange={(e) => {
                  setFeatures((prev) => ({ ...prev, [key]: e.target.value }));
                  console.log(`Updated ${key}:`, e.target.value); // Debugging log
                }}
              />
            </div>
          ))}
          <button onClick={handlePrediction}>Predict</button>
          {prediction && <h3>Result: {prediction}</h3>}
        </div>
      )}
    </div>
  );
};

export default App;
