import React, { useState } from "react";
import "./Auth.css";

const Auth = () => {
  const [isSignUp, setIsSignUp] = useState(true);

  return (
    <div className="auth-container">
      <div className="auth-overlay"></div>

      <div className="auth-card">
        <h2 className="auth-title">{isSignUp ? "Sign Up" : "Sign In"}</h2>
        <input type="email" className="auth-input" placeholder="Email" />
        <input type="password" className="auth-input" placeholder="Password" />

        {isSignUp && <input type="text" className="auth-input" placeholder="Full Name" />}

        <button className="auth-button">{isSignUp ? "Register" : "Login"}</button>

        <p className="auth-toggle" onClick={() => setIsSignUp(!isSignUp)}>
          {isSignUp ? "Already have an account? Sign In" : "New here? Sign Up"}
        </p>
      </div>
    </div>
  );
};

export default Auth;
