import React, { useState } from "react";
import axios from "axios";
import "./styles.css";

const App = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loggedIn, setLoggedIn] = useState(false);
  const [prediction, setPrediction] = useState(null);

  // Define features with proper initial values
  const [features, setFeatures] = useState({
    Age: "",
    BMI: "",
    DietQuality: "",
    SleepQuality: "",
    CholesterolTotal: "",
    CholesterolHDL: "",
    CholesterolTriglycerides: "",
    MMSE: "",
    FunctionalAssessment: "",
    MemoryComplaints: "",
    ADL: "",
    BehavioralProblems: "",
  });

  const toggleAuth = () => setIsLogin(!isLogin);

  const handleAuth = async () => {
    const endpoint = isLogin ? "login" : "signup";
    try {
      const response = await axios.post(`http://127.0.0.1:5000/${endpoint}`, {
        email,
        password,
      });
      alert(response.data.message);
      if (isLogin) setLoggedIn(true);
    } catch (error) {
      alert(error.response?.data?.message || "Authentication failed");
    }
  };

  const handlePrediction = async () => {
    try {
      const featureValues = Object.values(features).map(Number); // Convert all inputs to numbers
      const response = await axios.post("http://127.0.0.1:5000/predict", {
        features: featureValues,
      });
      setPrediction(response.data.prediction);
    } catch (error) {
      alert("Prediction failed. Check the inputs and try again.");
    }
  };

  return (
    <div className="container">
      {!loggedIn ? (
        <div className="auth-box">
          <h2>{isLogin ? "Login" : "Sign Up"}</h2>
          <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <button onClick={handleAuth}>{isLogin ? "Login" : "Sign Up"}</button>
          <p onClick={toggleAuth} className="toggle-text">
            {isLogin ? "Don't have an account? Sign Up" : "Already have an account? Login"}
          </p>
        </div>
      ) : (
        <div className="prediction-box">
          <h2>Alzheimerâ€™s Disease Detection</h2>
          {Object.keys(features).map((key, index) => (
            <div key={index} className="input-group">
              <label>{key}:</label>
              <input
                type="number"
                placeholder={`Enter ${key}`}
                value={features[key]}
                onChange={(e) => setFeatures({ ...features, [key]: e.target.value })}
              />
            </div>
          ))}
          <button onClick={handlePrediction}>Predict</button>
          {prediction && <h3>Result: {prediction}</h3>}
        </div>
      )}
    </div>
  );
};

export default App;
