import React, { useState } from "react";
import axios from "axios";
import "./Prediction.css";

const Prediction = () => {
  const [features, setFeatures] = useState({
    Age: "", 
    BMI: "",
    DietQuality: "",
    SleepQuality: "",
    CholesterolTotal: "",
    CholesterolHDL: "",
    CholesterolTriglycerides: "",
    MMSE: "",
    FunctionalAssessment: "",
    MemoryComplaints: "",
    ADL: "",
    BehavioralProblems: "",
  });

  const [prediction, setPrediction] = useState(null);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    setFeatures({ ...features, [e.target.name]: e.target.value });
  };

  const handlePrediction = async () => {
    try {
        // Convert features to an array of numbers and check for empty values
        const featureValues = Object.values(features).map(value => 
            value === "" ? null : Number(value)  
        );

        // Ensure no missing inputs
        if (featureValues.includes(null)) {
            setError("Please fill in all fields before predicting.");
            return;
        }

        console.log("Sending features:", featureValues);  // Debugging log

        // Send the data to Flask backend
        const response = await axios.post("http://127.0.0.1:5000/predict", {
            features: featureValues
        });

        console.log("Prediction Response:", response.data);

        setPrediction(response.data.result);
        setError(null);  // Clear errors if successful
    } catch (error) {
        setError("Prediction failed. Check the inputs and try again.");
    }
};

  return (
    <div className="prediction-container">
      <h2 className="prediction-title">Alzheimerâ€™s Disease Prediction</h2>
      <div className="input-form">
        {Object.keys(features).map((key, index) => (
          <div key={index} className="input-group">
            <label htmlFor={key}>{key}:</label>
            <input 
              id={key} 
              type="number" 
              name={key} 
              placeholder={`Enter ${key}`} 
              value={features[key]} 
              onChange={handleChange} 
            />
          </div>
        ))}
      </div>
      <button className="predict-btn" onClick={handlePrediction}>Predict</button>
      {error && <div className="error-box">{error}</div>}
      {prediction && <div className="result-box">Result: {prediction}</div>}
    </div>
  );
};

export default Prediction;
